package cloudservices.client;


import java.io.IOException;
import java.net.URL;
import java.util.Collection;
import java.util.Map;
import java.util.concurrent.ArrayBlockingQueue;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.ConcurrentLinkedQueue;
import java.util.concurrent.Executors;
import java.util.concurrent.ScheduledExecutorService;
import java.util.concurrent.ScheduledFuture;
import java.util.concurrent.TimeUnit;

import cloudservices.client.http.HTTPClientService;
import cloudservices.client.http.async.AsyncHttpConnection;
import cloudservices.client.http.async.StringResponseHandler;
import cloudservices.client.http.async.support.ParamsWrapper;
import cloudservices.client.listeners.AckPacketListener;
import cloudservices.client.listeners.HttpPacketListener;
import cloudservices.client.listeners.SubPacketListener;
import cloudservices.client.mqtt.PahoMQTTClientService;
import cloudservices.client.packets.AckPacket;
import cloudservices.client.packets.HttpPacket;
import cloudservices.client.packets.ListenerWrapper;
import cloudservices.client.packets.Packet;
import cloudservices.client.packets.PacketCollector;
import cloudservices.client.packets.PacketFilter;
import cloudservices.client.packets.PacketListener;
import cloudservices.client.packets.PacketReader;
import cloudservices.client.packets.PacketWriter;
import cloudservices.client.packets.TextPacket;
import cloudservices.client.packets.filters.PacketAckFilter;

/**                                .
 * ClientService类时客户端的主类，作为调用的入口
 * 单例形式存在
 * @author xanthodont
 *
 */
public class ClientService {
	public static final int STATUS_CONFIGED = 1;
	public static final int STATUS_STARTUP = 2;
	public static final int STATUS_CONNECTED = 3;
	
	
	/** 在线情况 */
	private volatile boolean online = false;
	private volatile boolean reconnecting = false;
	/** 当前使用的客户端 true表示mqtt，false表示http*/
	private volatile boolean actualClient = true;
	/** 单例实现 */
	private static class ProviderHolder {
		static ClientService instance = new ClientService();
	}
	
	/** 重连定时器 */
	private ScheduledExecutorService reconnectScheduler;
	/** 重连次数记录 */
	private long reconnects = 0;
	/** 重连线程 */
	final Runnable reconnectDeamon = new Runnable() {
		@Override
		public void run() {
			
			try {
				config(config);
			} catch (ConfigException e1) {
				// TODO Auto-generated catch block
				e1.printStackTrace();
			}
			connect();
		}
	};
	
	/** 接口调用顺序<br/>
	 *                                          |--> sendPacket()   -->|<br/>
	 * 	config() --> startup() --> connect() -->|					   |--> shutdown()<br/>
	 * 	                                        |<-- receivePacket()<--|<br/>
	 * @return
	 */
	public static ClientService getInstance() {
		return ProviderHolder.instance;
	}
	
	/** 配置信息类 */
	private ClientConfiguration config;
	/** 配置状态 */
	private int status = 0; 
	/** mqtt客户端 */
	private PahoMQTTClientService mqttClient;
	/** http客户端 */
	private HTTPClientService httpClient;
	
	private BlockingQueue<Packet> readerQueue;
	
	/** 消息包写入器 */
	private PacketWriter packetWriter;
	/** 消息包读取器 */
	private PacketReader packetReader;
	
	/** 连接监听器 */
	private Collection<ConnectionListener> connectionListeners = new ConcurrentLinkedQueue<ConnectionListener>();
	/** 回发包器集合，通过设置PacketFilter拦截回发包，然后做相应的处理 */
	protected final Collection<PacketCollector> collectors = new ConcurrentLinkedQueue<PacketCollector>();
	
	protected final Map<PacketListener, ListenerWrapper> recvListeners =
            new ConcurrentHashMap<PacketListener, ListenerWrapper>();
	private ScheduledFuture<?> reconnHandler;
	
	private ClientService() {
		init();
	}
	private void init() {
		mqttClient = new PahoMQTTClientService(this);
		httpClient = new HTTPClientService(this);
		
		this.packetWriter = new PacketWriter(this);
		this.packetReader = new PacketReader(this);
		startup();
		
		reconnectScheduler = Executors.newScheduledThreadPool(1);
		
		this.addConnectionListener(new DefaultConnectionListener(this));
		
		/** 配置回发Ack消息监听器 */
		PacketFilter ackFilter = new PacketFilter() {
			@Override
			public boolean accept(Packet packet) {
				// TODO Auto-generated method stub
				return packet.isAck() && (packet.getPacketType() == Packet.TEXT || packet.getPacketType() == Packet.FILE);
			}
		};
		this.addPacketListener(new AckPacketListener(this), ackFilter);
		/** 配置Http消息监听器 */
		PacketFilter httpFilter = new PacketFilter() {
			@Override
			public boolean accept(Packet packet) {
				// TODO Auto-generated method stub
				return packet.getPacketType() == Packet.HTTP;
			}
		};
		this.addPacketListener(new HttpPacketListener(this), httpFilter);
		/** 分段消息处理监听器 */
		PacketFilter subFilter = new PacketFilter() {
			@Override
			public boolean accept(Packet packet) {
				// TODO Auto-generated method stub
				return packet.isSub() && packet.getPacketType() == Packet.SUB;
			}
		};
		this.addPacketListener(new SubPacketListener(this), subFilter);
	}
	
	public void config(final ClientConfiguration config) throws ConfigException {
		/** 根据配置信息初始化相应的类 */
		this.config = config;
		switch (config.getConnectType()) {
		case 1: // 短连接
			httpClient.config(config);
			actualClient = false;
			break;
		case 2: // 长连接
			mqttClient.config(config);
			actualClient = true;
			break;
		case 3: // both
		default:
			mqttClient.config(config);
			httpClient.config(config);
			actualClient = true;
			break;
		}
		
		listenerConfigSuccess();
	}
	
	
	public ClientConfiguration getConfiguration() {
		return config;
	}
	
	private void startup() {
		packetWriter.startup();
		packetReader.startup();
		listenerStartWriterAndReader();
	}
	public void shutdown() {
		//packetWriter.shutdown();
		//packetReader.shutdown();
		
		getActualClient().disconnect();
		online = false;
		if (reconnHandler != null) {
			reconnHandler.cancel(false);
		}
		listenerConnectionClosed();
		
	}
	
	public void connect() {
		try {
			getActualClient().connect();  // 线程等待，知道连接完成或超时
			
			reconnects = 0; // 重置重连次数
			online = true;
			listenerConnectionSuccessful();
		} catch (ConnectException e) {
			// TODO Auto-generated catch block
			//e.printStackTrace();
			listenerConnectionFail(e);
			reconnecting = false;
			if (reconnects < config.getReconnectAttemptsMax()) {
				reconnect();
			} 
		} finally {
			reconnecting = false;
		}
		
	}
	
	public void sendPacket(Packet packet, String public2Topic) {
		packet.setPublic2Topic(public2Topic);
		packet.setUsername(config.getUsername());
		int size = getConfiguration().getBufferSize();
    	if (packet.toByteArray().length > size) {  // 分段消息发送
    		Packet[] packets = Packet.subsection(packet, size);  // 将消息转换成分段消息
    		for (int i = 0; i < packets.length; i++) {
				Packet subPacket = packets[i];
				packetWriter.sendPacket(subPacket);
			}
    	} else {
    		packetWriter.sendPacket(packet);
    	}
	}
	
	/**
	 * 重连
	 */
	public void reconnect() {
		if (isOnline() || reconnecting) { 
			System.out.printf("-- 当前在线或正在重连中，不做任何操作 -- online:%b, reconnecting:%b\n", online, reconnecting);
			//shutdown();
			return;
		}
		if (reconnHandler != null) {
			reconnHandler.cancel(false);
		}
		
		reconnects += 1;
		long reconnectDelay = config.getReconnectDelay();
        if( reconnectDelay> 0 && config.getReconnectBackOffMultiplier() > 1.0 ) {
            reconnectDelay = (long) Math.pow(config.getReconnectDelay()*reconnects, config.getReconnectBackOffMultiplier());
        }
        reconnectDelay = Math.min(reconnectDelay, config.getReconnectDelayMax());
        
        // 重连信息回调
		listenerReconnectStart(reconnects, reconnectDelay);
		
		if (reconnects <= config.getReconnectAttemptsMax()) {
			reconnecting = true;
			reconnHandler = reconnectScheduler.schedule(reconnectDeamon, reconnectDelay, TimeUnit.SECONDS);
		} else {
			System.out.println("-- 重连结束");
		}
	}
	
	public ISender getActualClient() {
		if (actualClient) 
			return mqttClient; 
		return httpClient;
	}
	
	public PacketReader getPacketReader() {
		return packetReader;
	}
	
	BlockingQueue<Packet> getReaderQueue() {
		return readerQueue;
	}
	public void removePacketCollector(PacketCollector packetCollector) {
		// TODO Auto-generated method stub
		
	}
	public PacketCollector createPacketCollector(PacketFilter packetFilter) {
		PacketCollector collector = new PacketCollector(this, packetFilter);
        // Add the collector to the list of active collectors.
        collectors.add(collector);
        return collector;
	}
	public Collection<PacketCollector> getPacketCollectors() {
		// TODO Auto-generated method stub
		return collectors;
	}
	
	/**
     * Registers a packet listener with this connection. A packet filter determines
     * which packets will be delivered to the listener. If the same packet listener
     * is added again with a different filter, only the new filter will be used.
     * 
     * @param packetListener the packet listener to notify of new received packets.
     * @param packetFilter   the packet filter to use.
     */
    public void addPacketListener(PacketListener packetListener, PacketFilter packetFilter) {
        if (packetListener == null) {
            throw new NullPointerException("Packet listener is null.");
        }
        ListenerWrapper wrapper = new ListenerWrapper(packetListener, packetFilter);
        recvListeners.put(packetListener, wrapper);
    }

    /**
     * Removes a packet listener for received packets from this connection.
     * 
     * @param packetListener the packet listener to remove.
     */
    public void removePacketListener(PacketListener packetListener) {
        recvListeners.remove(packetListener);
    }
	/**
     * 包接收器集合
     * 
     * @return a map of all packet listeners for received packets.
     */
    public Map<PacketListener, ListenerWrapper> getPacketListeners() {
        return recvListeners;
    }
    
    
    public void addConnectionListener(ConnectionListener connectionListener) {
        if (connectionListener == null) {
            return;
        }
        if (!connectionListeners.contains(connectionListener)) {
            connectionListeners.add(connectionListener);
        }
    }
    public void removeConnectionListener(ConnectionListener connectionListener) {
        connectionListeners.remove(connectionListener);
    }
    protected Collection<ConnectionListener> getConnectionListeners() {
        return connectionListeners;
    }

    public boolean isOnline() {
    	return online;
    }
    private void setOnline(boolean value) {
    	this.online = value;
    }
    public String getConnectType() {
    	if (actualClient) return "长连接";
    	return "短连接";
    }
    
    private void listenerConfigSuccess() {
    	for (ConnectionListener connectionListener : connectionListeners) {
			connectionListener.configSuccess(config);
		}
    }
    private void listenerStartWriterAndReader() {
    	for (ConnectionListener connectionListener : connectionListeners) {
			connectionListener.startWriterAndReader();
		}
    }
    private void listenerConnectionSuccessful() {
    	for (ConnectionListener connectionListener : connectionListeners) {
			connectionListener.connectionSuccessful();
		}
    }
    private void listenerConnectionFail(ConnectException e) {
    	for (ConnectionListener connectionListener : connectionListeners) {
    		connectionListener.connectionFail(e);
    	}
    }
    private void listenerReconnectStart(long reconnectTimes, long reconnectDelay) {
    	for (ConnectionListener connectionListener : connectionListeners) {
			connectionListener.reconnectStart(reconnectTimes, reconnectDelay);
		}
    }
    private void listenerConnectionClosed() {
    	for (ConnectionListener connectionListener : connectionListeners) {
			connectionListener.connectionClosed();
		}
    }
}
